const axios = require('axios');
const fs = require('fs');

// GitHub username
const username = 'mohamedcharfi';

// GitHub API endpoint for repositories
const apiEndpoint = `https://api.github.com/users/${username}/repos`;

async function fetchAllRepositories() {
    try {
        const response = await axios.get(apiEndpoint);
        return response.data;
    } catch (error) {
        console.error('Error fetching repositories:', error);
        return [];
    }
}

async function fetchReadme(repoName) {
    try {
        const readmeEndpoint = `https://api.github.com/repos/${username}/${repoName}/readme`;
        const response = await axios.get(readmeEndpoint, {
            headers: { Accept: 'application/vnd.github.v3.raw' } // To get the raw README content
        });
        return response.data;
    } catch (error) {
        console.error(`Error fetching README for ${repoName}:`, error);
        return '';
    }
}

function extractDemoLink(readmeContent) {
    // Simple regex to match URLs in the README content (adjust as needed)
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    const matches = readmeContent.match(urlRegex);

    // Return the first match or null if none found
    return matches ? matches[0] : null;
}

function createProjectEntry(id, repoData, demoLink) {
    const imageUrl = `https://raw.githubusercontent.com/${username}/${repoData.name}/main/assets/project-image.svg`; // Example image path
    return {
        id: id,
        projectName: repoData.name,
        projectDesc: repoData.description || 'No description available.',
        tags: [], // Tags can be manually added or inferred based on project details
        code: repoData.html_url,
        demo: demoLink || repoData.html_url, // Fallback to repo URL if no demo link found
        image: imageUrl // Direct URL to the image in the repository
    };
}

async function generateProjectsData() {
    const repositories = await fetchAllRepositories();
    let projectsData = [];

    for (const [index, repo] of repositories.entries()) {
        const readmeContent = await fetchReadme(repo.name);
        const demoLink = extractDemoLink(readmeContent);
        const projectEntry = createProjectEntry(index + 1, repo, demoLink);
        projectsData.push(projectEntry);
    }

    // Convert to JavaScript export format
    const projectsDataJS = `export const projectsData = ${JSON.stringify(projectsData, null, 4).replace(/\"/g, "'")};`;

    // Save to file
    fs.writeFileSync('projectsData.js', projectsDataJS);
    console.log('projectsData.js file generated successfully.');
}

generateProjectsData();
